mapper.select:
SELECT id FROM face
WHERE
origami_id = #{origamiId}
AND
id_in_origami = #{faceIdInOrigami}

mapper.insert:
INSERT INTO face (
    origami_id,
    face_id_in_origami,
    step_id
    , created_by
    , updated_by
    , created_at
    , updated_at
) VALUES (
    #{face.origamiId},
    #{face.faceIdInOrigami},
    #{face.stepId}
    , #{face.createdBy}
    , #{face.updatedBy}
    , #{face.createdAt}
    , #{face.updatedAt}
);
