mapper.select:
SELECT id FROM origami_point
WHERE
face_id = #{faceId}
AND
id_in_face = #{idInFace}
LIMIT 1

SELECT id FROM origami_point
WHERE face_id = #{faceId}
AND id_in_face IN (

    #{idInFace}

)

mapper.insert:
INSERT INTO origami_point (
    step_id,
    face_id,
    point_type_id,
    x_pos,
    y_pos,
    id_in_face
    , deleted_step_id
    , created_by
    , updated_by
    , created_at
    , updated_at
) VALUES (
    #{origamiPoint.stepId},
    #{origamiPoint.faceId},
    #{origamiPoint.pointTypeId},
    #{origamiPoint.xPos},
    #{origamiPoint.yPos},
    #{origamiPoint.idInFace}
    , #{origamiPoint.deletedStepId}
    , #{origamiPoint.createdBy}
    , #{origamiPoint.updatedBy}
    , #{origamiPoint.createdAt}
    , #{origamiPoint.updatedAt}
)

mapper.update:
UPDATE origami_point
SET deleted_step_id = #{deletedStepId}
WHERE face_id = #{faceId}
AND deleted_step_id IS NULL
AND id_in_face IN (

    #{idInFace}

)
